    this.setState({ loading: true });
    const { activeItem } = this.state;
    authAxios
      .get(addressListURL(activeItem === "S"))
      // .get(addressListURL(activeItem === "billingAddress" ? "B" : "S"))
      .then(res => {
        this.setState({ addresses: res.data, loading: false });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleCallback = () => {
    this.handleFetchAddresses();
  };

  renderAddresses = () => {
    const { activeItem, addresses, selectedAddress, userID } = this.state;
    // const { isAuthenticated } = this.props;
    // console.log(isAuthenticated);

    return (
      // <Container className={classes.cardGrid}>
      <div>
        <Grid container spacing={1} maxwidth="md">
          {addresses.map(a => {
            return (
              // <React.Fragment>
              <Grid item key={a.id} xs={12} sm={6} md={4}>
                <Card>
                  <CardContent>
                    {a.default && (
                      <Badge badgeContent={"Default"} color="primary"></Badge>
                    )}
                    {/* <FormControlLabel
                        value="best"
                        control={<Radio />}
                        label="Default Address"
                        name="default"
                        onChange={this.handleToggleDefault}
                        checked={formData.default}
                      /> */}
                    <Typography variant="h5" component="h2">
                      {a.place}
                    </Typography>
                    <br />
                    <Typography color="textPrimary">
                      {a.house_name} {a.area} {a.roa}
                    </Typography>
                    <Typography variant="body2" component="p">
                      {a.village}
                      <br />
                      {a.pin_code}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <IconButton onClick={() => this.handleSelectAddress(a)}>
                      <EditIcon />
                    </IconButton>
                    <IconButton
                      aria-label="Delete"
                      onClick={() => this.handleDeleteAddress(a.id)}
                    >
                      <DeleteSharpIcon />
                    </IconButton>
                  </CardActions>
                </Card>
              </Grid>
              // </React.Fragment>
            );
          })}
        </Grid>
      </div>
    );
  };

  // handleItemClick1 = e => {
  //   this.setState({ activeItem: e.target.name });
  //   console.log(this.state.activeItem);
  // };

  handleItemClick1 = (event, value) => {
    this.setState({ activeItem: value });
    console.log(this.state.activeItem);
  };

  render() {
    const { classes } = this.props;
    // const bull = <span className={classes.bullet}>â€¢</span>;
    const {
      activeItem,
      error,
      loading,
      userID,
      addresses,
      selectedAddress
      // places
    } = this.state;
    console.log(activeItem);

    // const { isAuthenticated } = this.props;
    // console.log(isAuthenticated);
    // if (!isAuthenticated) {
    //   return <Redirect to="/login" />;
    // }
    return (
      <React.Fragment>
        <Container className={classes.cardGrid} maxwidth="md">
          {loading && (
            <div
              align="middle"
              className={classes.loading}
              style={{ display: "flex", justifyContent: "center" }}
            >
              <CircularProgress />
            </div>
          )}
          {error && (
            <Alert severity="error">
              <AlertTitle>{JSON.stringify(error)}</AlertTitle>
              This is an error alert â€” <strong>check it out!</strong>
            </Alert>
          )}
          <Paper square className={classes.tab}>
            <Tabs
              variant="fullWidth"
              indicatorColor="secondary"
              textColor="secondary"
              aria-label="icon label tabs example"
              value={this.state.activeItem}
              onChange={this.handleItemClick1}
            >
              <Tab
                icon={<ListIcon />}
                label="My Delivery Addresses "
                name="shippingAddress"
                // active={activeItem}
                // active={activeItem === "shippingAddress"}
                // onClick={() => this.handleItemClick("shippingAddress")}
              />
              <Tab
                icon={<AddIcon />}
                label="Add Address"
                name="addAddress"
                // active={activeItem}
                // active={activeItem === "addAddress"}
                // onClick={() => this.handleItemClick("addAddress")}
              />
            </Tabs>
          </Paper>
          <br></br>
          {activeItem === 1 ? (
            <AddressForm formType={CREATE_FORM} userID={userID} />
          ) : (
            this.renderAddresses()
          )}
          {selectedAddress && (
            <AddressForm
              // activeItem={activeItem}
              userID={userID}
              address={selectedAddress}
              formType={UPDATE_FORM}
              // places={places}
              // callback={this.handleCallback}
            />
          )}
        </Container>
      </React.Fragment>
    );
  }
}

