import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import MaterialTable from "material-table";
import TableContainer from "@material-ui/core/TableContainer";

// import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Container from "@material-ui/core/Container";
import CircularProgress from "@material-ui/core/CircularProgress";
import Grid from "@material-ui/core/Grid";
import DeleteIcon from "@material-ui/icons/Delete";
import IconButton from "@material-ui/core/IconButton";
import { Alert, AlertTitle } from "@material-ui/lab";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";

import { makeStyles, useTheme, withStyles } from "@material-ui/core/styles";
import { authAxios } from "../../utils";
import { orderListURL, orderDeleteURL } from "../../constants";

const styles = theme => ({
  table: {
    minWidth: 500
  },
  orderDetails: {
    background: "#3f51b5"
  },
  orderDetailCell: {
    background: "#00bcd4"
  },
  orderDetailTotal: {
    background: "#304ffe"
  }
});

class Customer extends React.Component {
  state = {
    orders: [],
    loading: false,
    error: null,
    message: null
  };

  componentDidMount() {
    this.FetchOrders();
    // console.log(orders);
  }

  FetchOrders = () => {
    this.setState({ loading: true });
    authAxios
      .get(orderListURL)
      .then(res => {
        this.setState({ orders: res.data, loading: false });
        // console.log(res.data);
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  deleteOrder = id => {
    authAxios
      .delete(orderDeleteURL(id))
      .then(res => {
        this.setState({ message: "Order Deleted" });
        this.handleCallback();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleCallback = () => {
    this.FetchOrders();
  };

  test = () => {
    console.log("tested");
  };

  render() {
    const { classes } = this.props;
    const { orders, loading, message } = this.state;
    // console.log(orders);

    const columns = [
      { title: "Order No", field: "id" },
      { title: "Shop Name", field: "shop_name" },
      { title: "Data", field: "start_date" },
      { title: "Status", field: "order_status" }
    ];

    return (
      <Container>
        {message && (
          <Alert severity="error">
            <AlertTitle>{message}</AlertTitle>
            {/* This is an error alert â€” <strong>check it out!</strong> */}
          </Alert>
        )}
        {loading && (
          <div
            align="middle"
            // className={classes.loading}
            style={{ display: "flex", justifyContent: "center" }}
          >
            <CircularProgress />
          </div>
        )}
        <Grid item xs={12}>
          <MaterialTable
            options={
              ({
                headerStyle: {
                  // backgroundColor: "#01579b",#bbdefb
                  backgroundColor: "#9fa8da",
                  color: "#212121"
                },
                rowStyle: {
                  backgroundColor: "#e8eaf6"
                }
              },
              {
                pageSize: 20,
                pageSizeOptions: [20, 50, 100]
              })
            }
            title="Orders"
            columns={columns}
            data={orders}
            pageSize={10}
            detailPanel={rowData => {
              const address = rowData;
              this.test();
              const orderItem = rowData.order_items;
              return (
                <div>
                  <Grid item xs={12}>
                    <TableContainer component={Paper}>
                      <Table
                        className={classes.table}
                        aria-label="spanning table"
                      >
                        <TableHead>
                          <TableRow className={classes.orderDetails}>
                            <TableCell align="center" colSpan={4}>
                              <Typography
                                variant="h5"
                                component="h5"
                                gutterBottom
                              >
                                Order Details
                              </Typography>
                              <Button
                                variant="contained"
                                color="secondary"
                                onClick={() => this.deleteOrder(rowData.id)}
                              >
                                Delete Order
                              </Button>
                            </TableCell>
                          </TableRow>
                          <TableRow className={classes.orderDetailCell}>
                            <TableCell>Item Name</TableCell>
                            <TableCell align="right">Item Price</TableCell>
                            <TableCell align="right">Quantity</TableCell>
                            <TableCell align="right">Total Price</TableCell>
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          {orderItem.map(row => (
                            <TableRow
                              key={row.desc}
                              className={classes.orderDetailCell}
                            >
                              <TableCell>{row.item.title}</TableCell>
                              <TableCell align="right">
                                {row.quantity}
                              </TableCell>
                              <TableCell align="right">
                                {row.quantity}
                              </TableCell>
                              <TableCell align="right">
                                {row.final_price}
                              </TableCell>
                            </TableRow>
                          ))}

                          <TableRow className={classes.orderDetailTotal}>
                            <TableCell rowSpan={3} />
                            <TableCell align="right" colSpan={2}>
                              Total
                            </TableCell>
                            <TableCell align="right">{rowData.total}</TableCell>
                          </TableRow>
                        </TableBody>
                      </Table>
                    </TableContainer>
                  </Grid>
                </div>
              );
            }}
          />
        </Grid>
      </Container>
    );
  }
}

const mapStateToProps = state => {
  return {
    isAuthenticated: state.auth.token !== null
  };
};

// export default connect(mapStateToProps)(Profile);

export default connect(mapStateToProps)(withStyles(styles)(Customer));
