class CheckoutForm extends Component {
  state = {
    data: null,
    loading: false,
    error: null,
    success: false,
    shippingAddresses: [],
    billingAddresses: [],
    selectedShippingAddress: null,
    shop: "",
    place: "",
    mode_of_payment: ""
  };

  componentDidMount() {
    this.handleFetchOrder();
    this.handleFetchShippingAddresses();
  }

  handleFetchShippingAddresses = () => {
    this.setState({ loading: true });
    authAxios
      .get(addressListURL("S"))
      .then(res => {
        this.setState({
          shippingAddresses: res.data.map(a => {
            return {
              key: a.id,
              text: `${a.place}, ${a.area}, ${a.road_name}`,
              value: a.id
            };
          }),
          selectedShippingAddress: this.handleGetDefaultAddress(res.data),

          loading: false
        });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  handleGetDefaultAddress = addresses => {
    const filteredAddresses = addresses.filter(el => el.default === true);
    if (filteredAddresses.length > 0) {
      return filteredAddresses[0].id;
    }
    return "";
  };

  handleFetchOrder = () => {
    this.setState({ loading: true });
    authAxios
      .get(orderSummaryURL)
      .then(res => {
        this.setState({ data: res.data, loading: false });
      })
      .catch(err => {
        if (err.response.status === 404) {
          this.props.history.push("/orders");
        } else {
          this.setState({ error: err, loading: false });
        }
      });
  };

  submit = ev => {
    ev.preventDefault();
    this.setState({ loading: true });
    const { selectedShippingAddress } = this.state;
    authAxios
      .post(checkoutURL, {
        selectedShippingAddress
      })
      .then(res => {
        this.setState({ loading: false, success: true });
        this.redirectToOrders();
      })
      .catch(err => {
        this.setState({ loading: false, error: err });
      });
  };

  redirectToOrders() {
    setTimeout(this.props.history.push("/orders"), 10000);
  }

  render() {
    const { classes } = this.props;
    const { refreshCart } = this.props;
    const {
      data,
      error,
      loading,
      success,
      shippingAddresses,
      selectedShippingAddress
    } = this.state;

    return (
      <div>
        {loading && (
          <div>
            <CircularProgress color="secondary" />
          </div>
        )}
        {error && (
          <Alert severity="error">
            <AlertTitle>{JSON.stringify(error)}</AlertTitle>
          </Alert>
        )}
        <OrderPreview data={data} />
        <Typography variant="h6" component="h2">
          Select Address
        </Typography>
        {shippingAddresses.length > 0 ? (
          <Select
            name="selectedShippingAddress"
            value={selectedShippingAddress}
            clearable
            options={shippingAddresses}
            selection
            onChange={event =>
              this.handleSelectChangeAddress(event.target.value)
            }
          />
        ) : (
          <p>
            You need to <Link to="/profile">add an address</Link>
          </p>
        )}

        {shippingAddresses.length < 1 ? (
          <p>You need to add an addresse to complete your purchase</p>
        ) : (
          <React.Fragment>
            {/* <Header>Confirm Order?</Header> */}
            {/* <h2>Confirm Order</h2> */}
            <Typography variant="h6" component="h2">
              Confirm Order
            </Typography>

            {success && (
              <Alert severity="success">
                <AlertTitle>{"Your Order is successful"}</AlertTitle>
                {/* This is an error alert — <strong>check it out!</strong> */}
              </Alert>
            )}
            <Button
              loading={loading}
              disabled={loading}
              primary
              onClick={this.submit}
              style={{ marginTop: "10px" }}
            >
              Submit
            </Button>
          </React.Fragment>
        )}
      </div>
    );
  }
}

const mapDispatchToProps = dispatch => {
  return {
    refreshCart: () => dispatch(fetchCart())
  };
};

export default connect(
  null,
  mapDispatchToProps
)(CheckoutForm);

