import React from "react";
import { connect } from "react-redux";
import axios from "axios";

import { Alert, AlertTitle } from "@material-ui/lab";
import { makeStyles, withStyles } from "@material-ui/core/styles";

import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import CircularProgress from "@material-ui/core/CircularProgress";

import CameraIcon from "@material-ui/icons/PhotoCamera";
import CssBaseline from "@material-ui/core/CssBaseline";
import Grid from "@material-ui/core/Grid";
import Toolbar from "@material-ui/core/Toolbar";
import Container from "@material-ui/core/Container";
import Link from "@material-ui/core/Link";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Avatar from "@material-ui/core/Avatar";
import ImageIcon from "@material-ui/icons/Image";
import WorkIcon from "@material-ui/icons/Work";
import BeachAccessIcon from "@material-ui/icons/BeachAccess";
import PostCard02 from "./theme";
import JssProvider from "react-jss/lib/JssProvider";
import { createGenerateClassName } from "@material-ui/core/styles";
import { MuiThemeProvider, createMuiTheme } from "@material-ui/core/styles";

import { fetchCart } from "../../store/actions/cart";
import { authAxios } from "../../utils";

import {
  addToCartURL,
  ShopProductListURL,
  orderItemDeleteURL,
  orderItemUpdateQuantityURL
} from "../../constants";

const muiBaseTheme = createMuiTheme();

const generateClassName = createGenerateClassName({
  dangerouslyUseGlobalCSS: true
});

const theme = {
  overrides: {
    MuiCard: {
      root: {
        "&.MuiPostCard--02": {
          borderRadius: muiBaseTheme.spacing.unit * 2, // 16px
          transition: "0.3s",
          boxShadow: "0px 14px 80px rgba(34, 35, 58, 0.2)",
          width: "95%",
          position: "relative",
          maxWidth: 800,
          marginLeft: "auto",
          overflow: "initial",
          background: "#ffffff",
          display: "flex",
          alignItems: "center",
          padding: `${muiBaseTheme.spacing.unit * 2}px 0`,
          "&:hover": {
            transform: "translateY(-3px)",
            boxShadow: "0 4px 20px 0 rgba(0,0,0,0.12)"
          },
          "& .MuiCardMedia-root": {
            flexShrink: 0,
            width: "40%",
            paddingTop: "48%",
            transform: "translateX(-24%)",
            boxShadow: "4px 4px 20px 1px rgba(252, 56, 56, 0.2)",
            borderRadius: muiBaseTheme.spacing.unit * 2, // 16
            backgroundImage: "linear-gradient(147deg, #fe8a39 0%, #fd3838 74%)",
            backgroundColor: muiBaseTheme.palette.common.white,
            overflow: "hidden",
            "&:after": {
              content: '" "',
              position: "absolute",
              top: 0,
              left: 0,
              width: "100%",
              height: "100%",
              backgroundImage:
                "linear-gradient(147deg, #fe8a39 0%, #fd3838 74%)",
              borderRadius: muiBaseTheme.spacing.unit * 2, // 16
              opacity: 0.5
            }
          },
          "& .MuiCardContent-root": {
            textAlign: "left",
            paddingLeft: 0,
            padding: muiBaseTheme.spacing.unit * 2
          },
          "& .MuiTypography--heading": {
            fontWeight: "bold"
          },
          "& .MuiTypography--subheading": {
            marginBottom: muiBaseTheme.spacing.unit * 2
          },
          "& .MuiButton--readMore": {
            backgroundImage: "linear-gradient(147deg, #fe8a39 0%, #fd3838 74%)",
            boxShadow: "0px 4px 32px rgba(252, 56, 56, 0.4)",
            borderRadius: 100,
            paddingLeft: 24,
            paddingRight: 24,
            color: "#ffffff"
          }
        }
      }
    }
  }
};

class ProductList extends React.Component {
  state = {
    loading: false,
    error: null,
    data: [],
    formData: {}
  };

  componentDidMount() {
    const {
      match: { params }
    } = this.props;
    this.setState({ loading: true });
    axios
      .get(ShopProductListURL(params.shopID))
      .then(res => {
        this.setState({ data: res.data, loading: false });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  }

  handleToggleForm = () => {
    const { formVisible } = this.state;
    this.setState({
      formVisible: !formVisible
    });
  };

  handleFormatData = formData => {
    // convert {colour: 1, size: 2} to [1,2] - they're all variations
    return Object.keys(formData).map(key => {
      return formData[key];
    });
  };

  handleAddToCart = (slug, shop, place) => {
    this.setState({ loading: true });
    const { formData } = this.state;
    const variations = this.handleFormatData(formData);
    authAxios
      .post(addToCartURL, { place, shop, slug, variations })
      .then(res => {
        this.props.refreshCart();
        this.setState({ loading: false });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  renderVariations = orderItem => {
    let text = "";
    orderItem.item_variations.forEach(iv => {
      text += `${iv.variation.name}: ${iv.value}, `;
    });
    return text;
  };

  handleRemoveQuantityFromCart = slug => {
    authAxios
      .post(orderItemUpdateQuantityURL, { slug })
      .then(res => {
        this.handleFetchOrder();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleRemoveItem = itemID => {
    authAxios
      .delete(orderItemDeleteURL(itemID))
      .then(res => {
        this.handleFetchOrder();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleChange = (e, { name, value }) => {
    const { formData } = this.state;
    const updatedFormData = {
      ...formData,
      [name]: value
    };
    this.setState({ formData: updatedFormData });
  };

  render() {
    const { data, error, formData, formVisible, loading } = this.state;
    const item = data;
    const { classes } = this.props;

    return (
      <JssProvider generateClassName={generateClassName}>
        <MuiThemeProvider theme={createMuiTheme(theme)}>
          <div>
            <CssBaseline />
            <Container className={classes.cardGrid} maxWidth="md">
              {loading && (
                <div className={classes.loading}>
                  <CircularProgress />
                  <CircularProgress color="secondary" />
                </div>
              )}
              {error && (
                <Alert severity="error">
                  <AlertTitle>{JSON.stringify(error)}</AlertTitle>
                  This is an error alert â€” <strong>check it out!</strong>
                </Alert>
              )}
              <Grid container spacing={4} maxWidth="md">
                {data.map(card => (
                  <Grid item key={card} xs={12} sm={6} md={4}>
                    <Card className={"MuiPostCard--02"}>
                      <CardMedia
                        style={{ height: 0, paddingTop: "56%" }}
                        className={"MuiCardMedia-root"}
                        image={card.image}
                        title="Image title"
                      />
                      <CardContent className={"MuiCardContent-root"}>
                        <Typography
                          className={"MuiTypography--heading"}
                          gutterBottom
                          variant="h5"
                          component="h2"
                        >
                          {card.title}
                        </Typography>
                        <Typography
                          className={"MuiTypography--date"}
                          variant={"overline"}
                        >
                          {card.category}
                        </Typography>
                      </CardContent>
                      <CardActions>
                        <Button
                          className={"MuiButton--readMore"}
                          size="small"
                          variant="contained"
                          color="primary"
                          onClick={() =>
                            this.handleAddToCart(card.slug, card.shop)
                          }
                        >
                          Add to cart
                        </Button>
                      </CardActions>
                    </Card>
                    {data.variations &&
                      data.variations.map(v => {
                        return (
                          <React.Fragment>
                            <List className={classes.root}>
                              <ListItem>
                                <ListItemAvatar>
                                  <Avatar>
                                    <ImageIcon />
                                  </Avatar>
                                </ListItemAvatar>
                                <ListItemText
                                  primary="Photos"
                                  secondary="Jan 9, 2014"
                                />
                              </ListItem>
                            </List>
                          </React.Fragment>
                        );
                      })}
                  </Grid>
                ))}
              </Grid>
            </Container>
          </div>
        </MuiThemeProvider>
      </JssProvider>
    );
  }
}

const mapDispatchToProps = dispatch => {
  return {
    refreshCart: () => dispatch(fetchCart())
  };
};

export default connect(
  null,
  mapDispatchToProps
)(withStyles(theme)(ProductList));
