import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Collapse from "@material-ui/core/Collapse";
import IconButton from "@material-ui/core/IconButton";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import KeyboardArrowDownIcon from "@material-ui/icons/KeyboardArrowDown";
import KeyboardArrowUpIcon from "@material-ui/icons/KeyboardArrowUp";
import Container from "@material-ui/core/Container";
import Icon from "@material-ui/core/Icon";

import { userIDURL, userTypeURL, orderListURL } from "../constants";

import { authAxios } from "../utils";

const useRowStyles = makeStyles({
  root: {
    "& > *": {
      borderBottom: "unset",
      margin: "20px",
      padding: "20px"
    },
    orderDtails: {
      borderBottom: "unset",
      margin: "300px",
      padding: "20px"
    }
  }
});

const Myapp = () => {
  const [loading, setLoading] = useState(false);
  const [orders, setOrders] = useState([]);
  const [userID, setUserID] = useState(null);
  const [userType, setuserType] = useState(null);

  useEffect(() => {
    const findUserType = async () => {
      setLoading(true);
      const res = await authAxios.get(userTypeURL);
      setuserType(res.data);
      setLoading(false);
    };

    findUserType();
  }, []);

  useEffect(() => {
    const findUserID = async () => {
      setLoading(true);
      const res = await authAxios.get(userIDURL);
      setUserID(res.data);
      setLoading(false);
    };

    findUserID();
  }, []);

  useEffect(() => {
    const fetchOrders = async () => {
      setLoading(true);
      const res = await authAxios.get(orderListURL);
      setOrders(res.data);
      setLoading(false);
    };

    fetchOrders();
  }, []);

  console.log(userType);
  console.log(userID);
  console.log(orders);

  return (
    <div>
      <h1>orders</h1>
    </div>
  );
};

