function CustomerTable(props) {
  const { orders } = props;
  const { UserType } = props;
  const classes = useRowStyles();

  return (
    <Container maxWidth="md">
      <TableContainer component={Paper}>
        <Table
          aria-label="collapsible table"
          className={classes.paperContainer}
        >
          <TableHead>
            {/* { UserType === "Customer" ? <p>customer</p> : <p>not</p>} */}
            <TableRow>
              <TableCell />
              <Typography variant="h1" component="h2">
                <TableCell align="right">Shop Name</TableCell>
                <TableCell align="right">Total Amount</TableCell>
                <TableCell align="right">Date</TableCell>
                <TableCell align="right">Order Status</TableCell>
              </Typography>
            </TableRow>
          </TableHead>
          <TableBody>
            {orders.map(order => (
              <Customer key={order.id} order={order} />
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
}

function Customer(props) {
  const { order } = props;
  //   const { orders } = props;
  const [open, setOpen] = React.useState(false);
  const classes = useRowStyles();

  return (
    <React.Fragment>
      <TableRow className={classes.root}>
        <TableCell>
          <IconButton
            aria-label="expand row"
            size="small"
            onClick={() => setOpen(!open)}
          >
            {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
          </IconButton>
        </TableCell>
        <TableCell component="th" scope="row">
          {order.shop_name}
        </TableCell>
        {/* <TableCell align="right">{order.shop_name}</TableCell> */}
        <TableCell align="right">{order.total}</TableCell>
        <TableCell align="right">{order.start_date}</TableCell>
        <TableCell align="right">{order.order_status}</TableCell>
      </TableRow>
      <TableRow>
        <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
          <Collapse in={open} timeout="auto" unmountOnExit>
            <Box margin={1}>
              <Typography variant="h6" gutterBottom component="div">
                Order Details
              </Typography>
              <Table size="small" aria-label="purchases">
                <TableHead>
                  <TableRow>
                    <TableCell>Item</TableCell>

                    <TableCell align="right">Quantity</TableCell>
                    <TableCell align="right">Total price</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {order.order_items.map(order_item => (
                    <TableRow key={order_item.id}>
                      <TableCell component="th" scope="row">
                        {order_item.item.title}
                      </TableCell>

                      <TableCell align="right">{order_item.quantity}</TableCell>
                      <TableCell align="right">
                        {order_item.final_price}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </Box>
          </Collapse>
        </TableCell>
      </TableRow>
    </React.Fragment>
  );
}

