import React from "react";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
import {
  Button,
  Card,
  Dimmer,
  Divider,
  Form,
  Grid,
  Header,
  Image,
  Label,
  Loader,
  Menu,
  Message,
  Segment,
  Select,
  Table
} from "semantic-ui-react";
import {
  countryListURL,
  addressListURL,
  addressCreateURL,
  addressUpdateURL,
  addressDeleteURL,
  userIDURL,
  paymentListURL,
  orderListURL
} from "../constants";
import { authAxios } from "../utils";

const UPDATE_FORM = "UPDATE_FORM";
const CREATE_FORM = "CREATE_FORM";

class AddressForm extends React.Component {
  state = {
    error: null,
    loading: false,
    formData: {
      address_type: "",
      apartment_address: "",
      country: "",
      default: false,
      id: "",
      street_address: "",
      user: 1,
      zip: ""
    },
    saving: false,
    success: false
  };

  componentDidMount() {
    const { address, formType } = this.props;
    if (formType === UPDATE_FORM) {
      this.setState({ formData: address });
    }
  }

  handleToggleDefault = () => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      default: !formData.default
    };
    this.setState({
      formData: updatedFormdata
    });
  };

  handleChange = e => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      [e.target.name]: e.target.value
    };
    this.setState({
      formData: updatedFormdata
    });
  };

  handleSelectChange = (e, { name, value }) => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      [name]: value
    };
    this.setState({
      formData: updatedFormdata
    });
  };

  handleSubmit = e => {
    this.setState({ saving: true });
    e.preventDefault();
    const { formType } = this.props;
    if (formType === UPDATE_FORM) {
      this.handleUpdateAddress();
    } else {
      this.handleCreateAddress();
    }
  };

  handleCreateAddress = () => {
    const { userID, activeItem } = this.props;
    const { formData } = this.state;
    authAxios
      .post(addressCreateURL, {
        ...formData,
        user: userID,
        address_type: "S"
        // address_type: activeItem === "billingAddress" ? "B" : "S"
      })
      .then(res => {
        this.setState({
          saving: false,
          success: true,
          formData: { default: false }
        });
        this.props.callback();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleUpdateAddress = () => {
    const { userID, activeItem } = this.props;
    const { formData } = this.state;
    authAxios
      .put(addressUpdateURL(formData.id), {
        ...formData,
        user: userID,
        address_type: "S"
        // address_type: activeItem === "billingAddress" ? "B" : "S"
      })
      .then(res => {
        this.setState({
          saving: false,
          success: true,
          formData: { default: false }
        });
        this.props.callback();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  render() {
    const { countries } = this.props;
    const { error, formData, success, saving } = this.state;
    return (
      <Form
        onSubmit={this.handleSubmit}
        success={success}
        error={error}
        noValidate
      >
        <Form.Input
          required
          name="house_name"
          placeholder="house_name"
          onChange={this.handleChange}
          value={formData.house_name}
        />
        <Form.Input
          required
          name="place"
          placeholder="Place"
          onChange={this.handleChange}
          value={formData.place}
        />
        <Form.Input
          required
          name="area"
          placeholder="area"
          onChange={this.handleChange}
          value={formData.area}
        />
        <Form.Input
          required
          name="road_name"
          placeholder="road_name"
          onChange={this.handleChange}
          value={formData.road_name}
        />
        <Form.Input
          required
          name="district"
          placeholder="district"
          onChange={this.handleChange}
          value={formData.district}
        />
        <Form.Input
          required
          name="state"
          placeholder="state"
          onChange={this.handleChange}
          value={formData.state}
        />
        <Form.Input
          required
          name="pin_code"
          placeholder="Pin code"
          onChange={this.handleChange}
          value={formData.pin_code}
        />
        <Form.Input
          required
          name="phone_num"
          placeholder="Phone Number"
          onChange={this.handleChange}
          value={formData.phone_num}
        />
        <Form.Checkbox
          name="default"
          label="Make this the default address?"
          onChange={this.handleToggleDefault}
          checked={formData.default}
        />

        {success && (
          <Message success header="Success!" content="Your address was saved" />
        )}
        {error && (
          <Message
            error
            header="There was an error"
            content={JSON.stringify(error)}
          />
        )}
        <Form.Button disabled={saving} loading={saving} primary>
          Save
        </Form.Button>
      </Form>
    );
  }
}

class Profile extends React.Component {
  state = {
    activeItem: "shippingAddress",
    addresses: [],
    userID: null,
    selectedAddress: null
  };

  componentDidMount() {
    this.handleFetchAddresses();
    this.handleFetchCountries();
    this.handleFetchUserID();
  }

  handleItemClick = name => {
    this.setState({ activeItem: name }, () => {
      this.handleFetchAddresses();
    });
  };

  handleGetActiveItem = () => {
    const { activeItem } = this.state;
    if (activeItem === "billingAddress") {
      return "Billing Address";
    } else if (activeItem === "shippingAddress") {
      return "Shipping Address";
    } else if (activeItem === "addAddress") {
      return "Shipping Address";
    }
    return "Payment History";
  };

  handleFormatCountries = countries => {
    const keys = Object.keys(countries);
    return keys.map(k => {
      return {
        key: k,
        text: countries[k],
        value: k
      };
    });
  };

  handleDeleteAddress = addressID => {
    authAxios
      .delete(addressDeleteURL(addressID))
      .then(res => {
        this.handleCallback();
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleSelectAddress = address => {
    this.setState({ selectedAddress: address });
  };

  handleFetchUserID = () => {
    authAxios
      .get(userIDURL)
      .then(res => {
        this.setState({ userID: res.data.userID });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleFetchCountries = () => {
    authAxios
      .get(countryListURL)
      .then(res => {
        this.setState({ countries: this.handleFormatCountries(res.data) });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleFetchAddresses = () => {
    this.setState({ loading: true });
    const { activeItem } = this.state;
    authAxios
      .get(addressListURL(activeItem === "billingAddress" ? "B" : "S"))
      .then(res => {
        this.setState({ addresses: res.data, loading: false });
      })
      .catch(err => {
        this.setState({ error: err });
      });
  };

  handleCallback = () => {
    this.handleFetchAddresses();
    this.setState({ selectedAddress: null });
  };

  renderAddresses = () => {
    const {
      activeItem,
      addresses,
      countries,
      selectedAddress,
      userID
    } = this.state;
    return (
      <div>
        <Grid container spacing={1} maxWidth="md">
          {addresses.map(a => {
            return (
              <React.Fragment>
                <Grid item key={a.id} xs={12} sm={6} md={4}>
                  <Card className={classes.card}>
                    <CardContent>
                      <FormControlLabel
                        value="best"
                        control={<Radio />}
                        label="Default Address"
                      />
                      <Typography variant="h5" component="h2">
                        {a.place}
                      </Typography>
                      <br />
                      <Typography className={classes.pos} color="textPrimary">
                        {a.house_name} {a.area} {a.roa}
                      </Typography>
                      <Typography variant="body2" component="p">
                        {a.village}
                        <br />
                        {a.pin_code}
                      </Typography>
                    </CardContent>
                    <CardActions>
                      <EditIcon onClick={props.handleSelectAddress(a)} />
                      <DeleteSharpIcon />
                      {/* <Button size="small">Learn More</Button> */}
                    </CardActions>
                  </Card>
                </Grid>
              </React.Fragment>
            );
          })}
        </Grid>
      </div>
    );
  };

  render() {
    const { activeItem, error, loading } = this.state;
    const { isAuthenticated } = this.props;
    // if (!isAuthenticated) {
    //   return <Redirect to="/login" />;
    // }
    return (
      <React.Fragment>
        <Container className={classes.cardGrid} maxWidth="md">
          {loading && (
            <div
              align="middle"
              className={classes.loading}
              style={{ display: "flex", justifyContent: "center" }}
            >
              <CircularProgress />
            </div>
          )}
          {error && (
            <Alert severity="error">
              <AlertTitle>{JSON.stringify(error)}</AlertTitle>
              This is an error alert â€” <strong>check it out!</strong>
            </Alert>
          )}
          <Paper square className={classes.tab}>
            <Tabs
              variant="fullWidth"
              indicatorColor="secondary"
              textColor="secondary"
              aria-label="icon label tabs example"
            >
              <Tab
                icon={<ListIcon />}
                label="My Delivery Addresses "
                name="Your Delivery Addresses"
                active={activeItem === "shippingAddress"}
                onClick={() => this.handleItemClick("shippingAddress")}
              />
              <Tab
                icon={<AddIcon />}
                label="Add Address"
                name="addAddress"
                active={activeItem === "addAddress"}
                onClick={() => this.handleItemClick("addAddress")}
              />
            </Tabs>
          </Paper>
          <br></br>
          {activeItem === "addAddress" ? (
            <AddressForm
              activeItem={activeItem}
              formType={CREATE_FORM}
              userID={userID}
              callback={this.handleCallback}
            />
          ) : (
            <RenderAddresses
              activeItem={activeItem}
              addresses={addresses}
              selectedAddress={selectedAddress}
              userID={userID}
              handleSelectAddress={this.handleSelectAddress}
            />
          )}
          {activeItem === "paymentHistory" ? null : this.renderAddresses()}
        </Container>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  return {
    isAuthenticated: state.auth.token !== null
  };
};

export default connect(mapStateToProps)(Profile);
