import React, { useState, useEffect, Component } from "react";
import MaterialTable from "material-table";
// import Container from "@material-ui/core/Container";
import TableContainer from "@material-ui/core/TableContainer";

import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
// import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TablePagination from "@material-ui/core/TablePagination";
import TableRow from "@material-ui/core/TableRow";
import Container from "@material-ui/core/Container";
import KeyboardArrowDownIcon from "@material-ui/icons/KeyboardArrowDown";
import KeyboardArrowUpIcon from "@material-ui/icons/KeyboardArrowUp";
import IconButton from "@material-ui/core/IconButton";
import Box from "@material-ui/core/Box";
import Collapse from "@material-ui/core/Collapse";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";

import { authAxios } from "../../utils";
import { orderListURL } from "../../constants";

function Test() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);

  const columns = [
    { label: "Shop Name", id: "shop_name" },
    { label: "Total Amount", id: "total" },
    { label: "Data", id: "start_date" },
    { label: "Status", id: "order_status" }
  ];

  useEffect(() => {
    const fetchOrders = async () => {
      setLoading(true);
      const res = await authAxios.get(orderListURL);
      setOrders(res.data);
      setLoading(false);
    };

    fetchOrders();
  }, []);

  const useStyles = makeStyles({
    root: {
      width: "100%"
    },
    container: {
      maxHeight: 900
    }
  });

  // export default function StickyHeadTable() {
  const classes = useStyles();
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);
  const [open, setOpen] = React.useState(false);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = event => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  return (
    <Container>
      <Paper className={classes.root}>
        <TableContainer className={classes.container}>
          <form className={classes.root} noValidate autoComplete="off">
            {/* <TextField id="standard-basic" label="Search" /> */}
            {/* <TextField id="filled-basic" label="Filled" variant="filled" /> */}
            <TextField
              id="outlined-basic"
              label="Search.."
              variant="outlined"
            />
          </form>
          <Table stickyHeader aria-label="sticky table">
            <TableHead>
              <TableRow>
                <TableCell />
                <TableCell align="center">Shop Name</TableCell>
                <TableCell align="right">Total Amount</TableCell>
                <TableCell align="right">Date</TableCell>
                <TableCell align="right">Order Status</TableCell>
              </TableRow>
            </TableHead>
            {/* <TableBody>
              {orders
                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                .map(order => {
                  return (
                    <TableRow className={classes.root}>
                      <TableCell>
                        <IconButton
                          aria-label="expand row"
                          size="small"
                          onClick={() => setOpen(!open)}
                        >
                          {open ? (
                            <KeyboardArrowUpIcon />
                          ) : (
                            <KeyboardArrowDownIcon />
                          )}
                        </IconButton>
                      </TableCell>
                      <TableCell component="th" scope="row">
                        {order.shop_name}
                      </TableCell>
                      <TableCell align="right">{order.total}</TableCell>
                      <TableCell align="right">{order.start_date}</TableCell>
                      <TableCell align="right">{order.order_status}</TableCell>
                    </TableRow>

                    // <TableRow
                    //   hover
                    //   role="checkbox"
                    //   tabIndex={-1}
                    //   key={row.code}
                    // >
                    //   {columns.map(column => {
                    //     const value = row[column.id];
                    //     return (
                    //       <TableCell key={column.id} align={column.align}>
                    //         {column.format && typeof value === "number"
                    //           ? column.format(value)
                    //           : value}
                    //       </TableCell>
                    //     );
                    //   })}
                    // </TableRow>
                  );
                })}
            </TableBody> */}
            <TableBody>
              {orders
                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                .map(order => (
                  <Table2 key={order.id} order={order} />
                ))}
            </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
          rowsPerPageOptions={[10, 25, 100]}
          component="div"
          count={orders.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onChangePage={handleChangePage}
          onChangeRowsPerPage={handleChangeRowsPerPage}
        />
      </Paper>
    </Container>
  );
}

function Table2(props) {
  const useRowStyles = makeStyles({
    root: {
      "& > *": {
        borderBottom: "unset",
        margin: "20px",
        padding: "20px"
      },
      orderDtails: {
        borderBottom: "unset",
        margin: "300px",
        padding: "20px"
      }
    }
  });
  const { order } = props;
  const [open, setOpen] = React.useState(false);
  const classes = useRowStyles();

  return (
    <React.Fragment>
      <TableRow className={classes.root}>
        <TableCell>
          <IconButton
            aria-label="expand row"
            size="small"
            onClick={() => setOpen(!open)}
          >
            {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
          </IconButton>
        </TableCell>
        <TableCell component="th" scope="row">
          {order.shop_name}
        </TableCell>
        <TableCell align="right">{order.total}</TableCell>
        <TableCell align="right">{order.start_date}</TableCell>
        <TableCell align="right">{order.order_status}</TableCell>
      </TableRow>
      <TableRow>
        <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
          <Collapse in={open} timeout="auto" unmountOnExit>
            <Box margin={1}>
              <Typography variant="h6" gutterBottom component="div">
                Order Details
              </Typography>
              <Table size="small" aria-label="purchases">
                <TableHead>
                  <TableRow>
                    <TableCell>Item</TableCell>

                    <TableCell align="right">Quantity</TableCell>
                    <TableCell align="right">Total price</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {order.order_items.map(order_item => (
                    <TableRow key={order_item.id}>
                      <TableCell component="th" scope="row">
                        {order_item.item.title}
                      </TableCell>

                      <TableCell align="right">{order_item.quantity}</TableCell>
                      <TableCell align="right">
                        {order_item.final_price}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </Box>
          </Collapse>
        </TableCell>
      </TableRow>
    </React.Fragment>
  );
}
export default Test;
